mapper.select:
select 

from salaries
where emp_no = #{empNo,jdbcType=INTEGER}
  and from_date = #{fromDate,jdbcType=DATE}

mapper.delete:
delete from salaries
where emp_no = #{empNo,jdbcType=INTEGER}
  and from_date = #{fromDate,jdbcType=DATE}

mapper.update:
update salaries


    salary = #{salary,jdbcType=INTEGER},


    to_date = #{toDate,jdbcType=DATE},


where emp_no = #{empNo,jdbcType=INTEGER}
  and from_date = #{fromDate,jdbcType=DATE}

update salaries
set salary = #{salary,jdbcType=INTEGER},
  to_date = #{toDate,jdbcType=DATE}
where emp_no = #{empNo,jdbcType=INTEGER}
  and from_date = #{fromDate,jdbcType=DATE}
